mapper.update:
update property set pdeposite=#{pdeposite}, prentalfee=#{prentalfee},
pfloortype=#{pfloortype}, pfloor=#{pfloor},
psize=#{psize}, pmaintenance=#{pmaintenance}, ptitle=#{ptitle},
pcategory=#{pcategory},
paddress=#{paddress}, paddressdetail=#{paddressdetail}, ppostcode=#{ppostcode},
platitude=#{platitude}, plongitude=#{plongitude}

	, pthumbnailoname=#{pthumbnailoname},
	pthumbnailtype=#{pthumbnailtype},
	pthumbnaildata=#{pthumbnaildata}

where pnumber=#{pnumber}

update property
set pstatus=#{pstatus}
where pnumber=#{pnumber}

update property
set phitcount=phitcount+1
where pnumber=#{pnumber}

mapper.select:
select
count(*)
from property

	pstatus='활성화'

		and pcategory=#{rentType}


		and pfloortype=#{floorType}


		and (
		paddress like '%' || #{keyword} || '%'
		or paddressdetail like '%' || #{keyword} || '%'
		)


		and PLATITUDE = #{platitude}
		and plongitude = #{plongitude}


select pnumber, pdeposite, prentalfee, pfloortype, pfloor, psize, pmaintenance, phitcount, pdate, pstatus, 
ptitle, pcategory, paddress, paddressdetail, ppostcode, platitude, plongitude, p_unumber
from (
    select p.*, row_number() over (order by p.pnumber desc) as rnum
    from property p
    where pstatus='활성화'
)
where rnum between #{offset} + 1 and #{offset} + #{limit}

select pnumber, pdeposite, prentalfee, pfloortype, pfloor, psize, pmaintenance, phitcount, pdate, pstatus, ptitle, pcategory, paddress, paddressdetail, ppostcode, platitude, plongitude, p_unumber from ( select p.*, row_number() over (order by p.pnumber desc) as rnum from property p
pstatus='활성화'
and pcategory=#{rentType}
and pfloortype=#{floorType}
and ( p.paddress like '%' || #{keyword} || '%' or p.paddressdetail like '%' || #{keyword} || '%' )
and PLATITUDE like #{platitude} || '%' and PLONGITUDE like #{plongitude} || '%'
pdeposite ASC,
pdeposite DESC,
pdate ASC,
pdate DESC,
pnumber DESC,
) where rnum between #{offset} + 1 and #{offset} + #{limit}


select rnum, pnumber,pdeposite,prentalfee, pfloortype, pfloor, psize, pmaintenance, pdate, pstatus, ptitle, pcategory, p_unumber
from (
		select rownum as rnum, pnumber,pdeposite,prentalfee, pfloortype, pfloor, psize, pmaintenance, pdate, pstatus, ptitle, pcategory, p_unumber, pthumbnaildata, pthumbnailoname, pthumbnailtype
from(
	select pnumber,pdeposite,prentalfee, pfloortype, pfloor, psize, pmaintenance, pdate, pstatus, ptitle, pcategory, p_unumber, pthumbnaildata, pthumbnailoname, pthumbnailtype
	from property
	where p_unumber=#{unumber}
	order by pnumber desc
)
where rownum <= #{pager.endRowNo}
)
where rnum >= #{pager.startRowNo}


select rnum, pnumber,pdeposite,prentalfee, pfloortype, pfloor, psize, pmaintenance, pdate, pstatus, ptitle, pcategory, p_unumber, pthumbnaildata, pthumbnailoname, pthumbnailtype
from (
		select rownum as rnum, pnumber,pdeposite,prentalfee, pfloortype, pfloor, psize, pmaintenance, pdate, pstatus, ptitle, pcategory, p_unumber, pthumbnaildata, pthumbnailoname, pthumbnailtype
from(
	select pnumber,pdeposite,prentalfee, pfloortype, pfloor, psize, pmaintenance, pdate, pstatus, ptitle, pcategory, p_unumber, pthumbnaildata, pthumbnailoname, pthumbnailtype
	from property 

	p_unumber=#{unumber}

		and pstatus='거래완료'


		and pstatus='활성화'


	order by pdate 

	desc


	asc


)
where rownum <= #{pager.endRowNo}
)
where rnum >= #{pager.startRowNo}

select pnumber from property
where platitude = #{platitude}
and plongitude = #{plongitude}
and pstatus != '거래완료'
